import axios from '../api/axios';
import React, { useState, useEffect } from 'react';
import requests from '../api/requests';
import './Banner.css';
export default function Banner() {
    const [movie, setMovie] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        /* 
            ko: 현재 상영중인 영화 정보를 가져오기(여러 영화)
            ja: 現在上映中の映画情報を取得(複数)
        */
        const request = await axios.get(requests.fetchNowPlaying);

        /* 
            ko: 여러 영화중 하나의 ID 가져오기
            ja: 複数の映画の中で一つのID取得
        */
        const movieId = request.data.results[
            Math.floor(Math.random() * request.data.results.length)
        ].id;

        /* 
            ko: 특정 영화의 더 상세한 정보를 가져오기(비디오 정보 포함)
            ja: 特定の映画のより詳細な情報を取得する（ビデオ情報を含む）
        */
        const { data: movieDetail } = await axios.get(`movie/${movieId}`, {
            params: { append_to_responce: 'videos' },
        });

        setMovie(movieDetail);
    }

    const truncate = (str, n) => {
        return str?.length > n ? str.substr(0, n - 1) + '...' : str;
    };

    return (
        <header className='banner'
            style={{
                backgroundImage: `url("https://image.tmdb.org/t/p/original/${movie?.backdrop_path}")`,
                backgroundPosition: 'top center',
                backgroundSize: 'cover',
            }}>
            <div className='banner__contents'>
        <h1 className='banner__title'>
            {movie?.title || movie?.name || movie?.orginal_name}
        </h1>
        <div className='banner__buttons'>
            <button className='banner__button play'>Play</button>
            <button className='banner__button info'>
                <div className='space'></div> More Information
            </button>
        </div>
        <h1 className='banner__description'>
            {truncate(movie?.overview, 300)}
        </h1>
    </div>
    <div className='banner--fadeBotton'></div>
        </header>
    )
}
