import axios from "axios";

const instabnce = axios.create({
    baseURL: "https://api.themoviedb.org/3",
    params: {
        api_key: process.env.REACT_APP_MOVIE_DB_KEY,
        /**
         * ko: 설정된 언어로 정보를 받아오기 위해 
         * ja: 設定した言語で情報を取得ため、
        */
        language: "ko-KR",
    }
})

export default instabnce;
